class PomodoroTimer {
    constructor() {
        this.workTime = 25 * 60; // 25 minutes in seconds
        this.breakTime = 5 * 60; // 5 minutes in seconds
        this.currentTime = this.workTime;
        this.isRunning = false;
        this.isWorkMode = true;
        this.timer = null;

        // DOM elements
        this.minutesDisplay = document.getElementById('minutes');
        this.secondsDisplay = document.getElementById('seconds');
        this.startButton = document.getElementById('start');
        this.pauseButton = document.getElementById('pause');
        this.resetButton = document.getElementById('reset');
        this.workButton = document.getElementById('work');
        this.breakButton = document.getElementById('break');
        this.soundButton = document.getElementById('toggleSound');
        this.soundOnIcon = this.soundButton.querySelector('.sound-on');
        this.soundOffIcon = this.soundButton.querySelector('.sound-off');
        this.natureSound = document.getElementById('natureSound');
        this.isSoundOn = false;

        // Event listeners
        this.startButton.addEventListener('click', () => this.start());
        this.pauseButton.addEventListener('click', () => this.pause());
        this.resetButton.addEventListener('click', () => this.reset());
        this.workButton.addEventListener('click', () => this.setWorkMode());
        this.breakButton.addEventListener('click', () => this.setBreakMode());
        this.soundButton.addEventListener('click', () => this.toggleSound());

        // Add new properties for animation
        this.sandLevel = 1;
        this.topSand = document.querySelector('.top-sand');
        this.bottomSand = document.querySelector('.bottom-sand');
        this.fallingSandContainer = document.querySelector('.falling-sand');
        this.totalSeconds = this.workTime;

        this.updateDisplay();
    }

    updateDisplay() {
        const minutes = Math.floor(this.currentTime / 60);
        const seconds = this.currentTime % 60;
        this.minutesDisplay.textContent = minutes.toString().padStart(2, '0');
        this.secondsDisplay.textContent = seconds.toString().padStart(2, '0');
        
        if (this.isRunning) {
            // Update sand levels
            this.sandLevel = this.currentTime / this.totalSeconds;
            
            // Update top sand (emptying)
            this.topSand.style.transform = `scaleY(${this.sandLevel})`;
            
            // Update bottom sand (filling)
            this.bottomSand.style.transform = `scaleY(${1 - this.sandLevel})`;
            
            // Create multiple falling sand particles
            if (this.currentTime % 1 === 0) {
                for (let i = 0; i < 3; i++) {
                    setTimeout(() => this.createSandParticle(), i * 150);
                }
            }
        }
    }

    start() {
        if (!this.isRunning) {
            this.isRunning = true;
            if (this.isSoundOn && this.natureSound.paused) {
                this.natureSound.play();
            }
            this.timer = setInterval(() => {
                this.currentTime--;
                this.updateDisplay();

                if (this.currentTime === 0) {
                    this.playAlarm();
                    this.reset();
                    if (this.isWorkMode) {
                        this.setBreakMode();
                    } else {
                        this.setWorkMode();
                    }
                }
            }, 1000);
        }
    }

    pause() {
        this.isRunning = false;
        clearInterval(this.timer);
        if (this.isSoundOn) {
            this.natureSound.pause();
        }
    }

    reset() {
        this.pause();
        this.currentTime = this.isWorkMode ? this.workTime : this.breakTime;
        this.totalSeconds = this.currentTime;
        this.sandLevel = 1;
        this.topSand.style.transform = `scaleY(1)`;
        this.bottomSand.style.transform = `scaleY(0)`;
        this.updateDisplay();
    }

    setWorkMode() {
        this.isWorkMode = true;
        this.workButton.classList.add('active');
        this.breakButton.classList.remove('active');
        this.currentTime = this.workTime;
        this.totalSeconds = this.workTime;
        this.sandLevel = 1;
        this.topSand.style.transform = `scaleY(1)`;
        this.bottomSand.style.transform = `scaleY(0)`;
        this.updateDisplay();
        this.pause();
    }

    setBreakMode() {
        this.isWorkMode = false;
        this.breakButton.classList.add('active');
        this.workButton.classList.remove('active');
        this.currentTime = this.breakTime;
        this.totalSeconds = this.breakTime;
        this.sandLevel = 1;
        this.topSand.style.transform = `scaleY(1)`;
        this.bottomSand.style.transform = `scaleY(0)`;
        this.updateDisplay();
        this.pause();
    }

    playAlarm() {
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();
    }

    createSandParticle() {
        const particle = document.createElementNS("http://www.w3.org/2000/svg", "circle");
        const xPos = 47 + Math.random() * 6; // More centered random position
        
        particle.setAttribute("cx", xPos);
        particle.setAttribute("cy", "65");
        particle.setAttribute("r", "0.8");
        particle.classList.add("sand-particle");
        
        this.fallingSandContainer.appendChild(particle);
        
        setTimeout(() => {
            if (this.fallingSandContainer.contains(particle)) {
                this.fallingSandContainer.removeChild(particle);
            }
        }, 500); // Reduced timeout to match animation duration
    }

    toggleSound() {
        this.isSoundOn = !this.isSoundOn;
        if (this.isSoundOn) {
            this.natureSound.play();
            this.soundOnIcon.classList.remove('hidden');
            this.soundOffIcon.classList.add('hidden');
        } else {
            this.natureSound.pause();
            this.soundOnIcon.classList.add('hidden');
            this.soundOffIcon.classList.remove('hidden');
        }
    }
}

// Initialize the timer when the page loads
document.addEventListener('DOMContentLoaded', () => {
    const pomodoro = new PomodoroTimer();
}); 